function update(){$.get("slideshow/next/"+latest,function(n){showImage(n.file);n.file>latest&&(latest=n.file);setTimeout(update,rotationMs)})}function showImage(n){$("#image").attr("src","slideshow/image/"+n)}var latest="",rotationMs=0;$.get("slideshow/init",function(n){latest=n.file;rotationMs=n.rotationMs;showImage(n.file);setTimeout(update,rotationMs)});